// 2) Write a java program to swap variables.

package assignments;

public class Swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int num1 = 46;
       int num2  = 18;
       int temp=0;
        temp =num1;
        num1 =num2;
        num2 = temp;
       
       System.out.println("After swapping:=" + num1 + ", ="+num2
    		   );
      
	}
	
}
